import InputAdornment from "@material-ui/core/InputAdornment";
import MenuItem from "@material-ui/core/MenuItem";
import Paper from "@material-ui/core/Paper";
import Chip from "@material-ui/core/Chip";
import Grid from "@material-ui/core/Grid";
import Typography from "@material-ui/core/Typography";
import WarningIcon from "@material-ui/icons/Warning";
import SortIcon from "@material-ui/icons/SortByAlpha";
import ShuffleIcon from "@material-ui/icons/Shuffle";
import SubjectIcon from "@material-ui/icons/LibraryBooks";
import PaidIcon from "@material-ui/icons/AttachMoney";

// redux imports
import { connect } from "react-redux";
import { getProfiles } from "../../redux/actions/profileActions";
import { getSubjects } from "../../redux/actions/subjectActions";

// MUI imports
import { withStyles } from "@material-ui/core/styles";
import { Input } from "@material-ui/core";

const styles = {
  card: {
    minWidth: 300,
  },
  padding20: {
    padding: 20,
  },
  marginLeft20: {
    marginLeft: 20,
  },
};

class ProfilesShowcase extends Component {
  constructor() {
    super();
    this.state = {
      data: [], // this is the current list of profiles
      allProfiles: [], // this is the full list of profiles
      searching: false, // whether or not we are searching
      searchData: [], // results from searching through all profiles
      filterByPaid: false, // whether or not we are filtering by paid
      filterByVolunteer: false, // whether or not we are filtering by volunteer
      filtering: false, // whether or not we are filtering
      searchText: "", // the text typed in the search bar
      orderDropDown: null, // order by dropdown
      filterDropDown: null, // paid or volunteer dropdown
      majorsDropDown: null, // majors dropdown
      subjects: [], // all subjects
      subjectFilters: [], // currentl list of selected subjects
      searchNotFound: false,
      shuffled: false, // whether or not we are shuffling
    };
  }

  //Functions then render
  componentDidMount() {
    this.props.getSubjects();
    this.props.getProfiles();

    if (sessionStorage.length > 0) {
      this.setState(
        {
          subjectFilters: JSON.parse(sessionStorage.getItem("subjectFilters")),
          searchData: JSON.parse(sessionStorage.getItem("searchData")),
          searchText:
            sessionStorage.getItem("searchText") === null
            ? ""
            : sessionStorage.getItem("searchText").replace(/[""]+/g, ""),
          filterByPaid: sessionStorage.getItem("filterByPaid") === "true",
          filterByVolunteer:
            sessionStorage.getItem("filterByVolunteer") === "true",
        },
        () => this.runAllFilters()
      );
    }
  }

  componentWillReceiveProps(nextProps) {
    if (nextProps.errors) this.setState({ errors: nextProps.errors });
    if (nextProps.subjects.subjects) {
      this.setState({
        subjects: _.sortBy(_.map(nextProps.subjects.subjects, "name")),
      });
    }
    if (nextProps.profile.profiles) {
      this.setState({
        allProfiles: nextProps.profile.profiles,
      });
    }
    if (sessionStorage.length < 1) {
      this.setState({ data: nextProps.profile.profiles });
    }
  }

  //for randomizing profiles displayed
  shuffle = (event) => {
    this.setState({
      data: _.shuffle(this.state.data),
      shuffled: true,
    });
  };

  closeOrderMenu = (event) => {
    this.setState({
      orderDropDown: null,
    });
  };

  closeFilterMenu = (event) => {
    this.setState({
      filterDropDown: null,
    });
  };

  closeMajorsMenu = (event) => {
    this.setState({
      majorsDropDown: null,
    });
  };

  handleFilterBy = (event) => {
    this.setState({
      filterDropDown: event.currentTarget,
    });
  };

  handleOrderBy = (event) => {
    this.setState({
      orderDropDown: event.currentTarget,
    });
  };

  handleMajorsMenu = (event) => {
    this.setState({
      majorsDropDown: event.currentTarget,
    });
  };

  handleCheck = (name) => (event) => {
    this.setState({
      [name]: event.target.checked,
    });
  };

  handleChange = (event) => {
    const searchText = event.target.value;
    this.setState({ searchText });
  };

  // sort currently displayed profiles by name alphabetically
  orderByName = (event) => {
    let nameSorted = _.orderBy(this.state.data, "user.firstname", "asc");

    this.setState((state) => ({
      data: nameSorted,
    }));
  };

  // sort by Major alphabetically
  orderByMajor = (event) => {
    let majorSorted = _.orderBy(this.state.data, "major[0]", "asc");

    this.setState((state) => ({
      data: majorSorted,
    }));
  };

  //uses current value of search bar to find tutors based on name, major, or the courses they tutor
  handleSearch = (event) => {
    const search_text = event.target.value.toLowerCase().trim();
    console.log('Search Text:', search_text);
    const { allProfiles, filtering } = this.state;
    console.log('All Profiles:', allProfiles);
  
    let profileList = filtering ? this.state.data : allProfiles;
  
    if (search_text.length === 0) {
      this.setState({ searching: false, searchData: [] }, () => this.runAllFilters());
      return;
    }
  
    const searchList = allProfiles.filter((profile) => {
      const { user, courses, major, minor } = profile;
  
      // Check if any course matches the search text
      const courseMatch = courses.some((course) => {
        const { courseNumber, courseId, courseSubject, courseName } = course;
        const courseString = `${courseId}${courseNumber}`.toLowerCase();
        return (
          courseNumber === search_text ||
          courseId.toLowerCase() === search_text ||
          courseString.includes(search_text) ||
          courseSubject.toLowerCase().includes(search_text) ||
          courseName.toLowerCase().includes(search_text)
        );
      });
  
      // Check if major or minor matches the search text
      const majorMinorMatch = [...major, ...minor].some((subject) =>
        subject.toLowerCase().includes(search_text)
      );
  
      // Check if user's first name matches the search text
      const firstNameMatch = user.firstname.toLowerCase().includes(search_text);
  
      return courseMatch || majorMinorMatch || firstNameMatch;
    });
    console.log('Search List:', searchList);
  
    if (searchList.length > 0) {
      this.setState({ searchData: searchList, searchNotFound: false, searching: true }, () =>
        this.runAllFilters()
      );
    } else {
      this.setState({ searchData: [], searchNotFound: true });
    }
  };
  

  //Returns results from profiles relating to chosen subject
  filterBySubject = (subject, event) => {
    let subjectFilterList = this.state.subjectFilters;
    subjectFilterList.push(subject);

    this.setState(
      {
        subjectFilters: subjectFilterList,
        filtering: true,
      },
      () => this.runAllFilters()
    );
  };

  filterByPaid = (paid) => (event) => {
    this.setState(
      {
        filterByPaid: true,
        filterByVolunteer: false,
      },
      () => this.runAllFilters()
    );
  };

  filterByVolunteer = (paid) => (event) => {
    this.setState(
      {
        filterByVolunteer: true,
        filterByPaid: false,
      },
      () => this.runAllFilters()
    );
  };

  removePaidFilter = (paid) => (event) => {
    this.setState({ filterByPaid: false }, () => this.runAllFilters());
  };

  removeVolunteerFilter = (paid) => (event) => {
    this.setState({ filterByVolunteer: false }, () => this.runAllFilters());
  };

  removeSubject = (subject, event) => {
    _.pull(this.state.subjectFilters, subject);
    this.setState(
      {
        subjectFilters: this.state.subjectFilters,
        searchNotFound: false,
      },
      () => this.runAllFilters()
    );
  };

  runAllFilters() {
    const {
      searchText,
      searchData,
      allProfiles,
      filterByPaid,
      filterByVolunteer,
      subjectFilters,
    } = this.state;
    // use the search data to start if there is search text, if not use the full list
    let profiles = searchText && searchText.length > 0 ? searchData : allProfiles;


    // check for paid and volunteer
    if (filterByPaid) {
      profiles = _.filter(profiles, { type: "Paid" });
    }

    if (filterByVolunteer) {
      profiles = _.filter(profiles, { type: "Volunteer" });
    }

    // check subjects
    let subjectFilterList = subjectFilters;
    if (subjectFilterList.length > 0) {
      let results = [];

      //this loop matches profiles to given subjects
      for (let prof in profiles) {
        let profile = profiles[prof];
        let majors = profile.major;
        let minors = profile.minor;
        let courses = profile.courses;

        for (let e in subjectFilterList) {
          let eachSubject = subjectFilterList[e];
          for (let m in majors) {
            //add profiles by major
            let major = majors[m];
            if (eachSubject === major && !results.includes(profile)) {
              results.push(profile);
            }
          }
          for (var mi in minors) {
            //add by minor
            let minor = minors[mi];
            if (eachSubject === minor && !results.includes(profile)) {
              results.push(profile);
            }
          }
          for (var c in courses) {
            //add by course subject
            let courseSubject = courses[c].courseSubject;
            if (eachSubject === courseSubject && !results.includes(profile)) {
              results.push(profile);
            }
          }
        }
      }
      if (results.length < 1) {
        this.setState({ searchNotFound: true });
      } else {
        this.setState({ searchNotFound: false });
      }
      this.setState({ data: results });
      profiles = results;
    }

    this.setState({ data: profiles });
  }

  saveFilters = () => {
    const {
      subjectFilters,
      searchText,
      searchData,
      filterByPaid,
      filterByVolunteer,
    } = this.state;
    sessionStorage.setItem("subjectFilters", JSON.stringify(subjectFilters));
    sessionStorage.setItem("searchText", JSON.stringify(searchText));
    sessionStorage.setItem("filterByPaid", JSON.stringify(filterByPaid));
    sessionStorage.setItem(
      "filterByVolunteer",
      JSON.stringify(filterByVolunteer)
    );
    sessionStorage.setItem("searchData", JSON.stringify(searchData));
  };

  render() {
    const { profiles, loading } = this.props.profile;
    const {
      orderDropDown,
      filterDropDown,
      majorsDropDown,
      subjects,
      data,
      searchNotFound,
    } = this.state;

    let courseMenuItems = subjects.map((subject, i) => (
      <MenuItem
        key={i}
        value={subject}
        disabled={_.includes(this.state.subjectFilters, subject)}
        onClick={(e) => this.filterBySubject(subject)}
      >
        {subject}
      </MenuItem>
    ));

    let subjectChips = this.state.subjectFilters && this.state.subjectFilters.map((subject, index) => (
      <Chip
        className="search-chip"
        variant="outlined"
        key={index}
        label={subject}
        onDelete={(e) => this.removeSubject(subject)}
      />
    ));

    let profileItems;

    const profileContent =
  data && !searchNotFound ? (
    data.map((profile) => (
      <ProfileItem
        key={profile._id}
        profile={profile}
        onClick={this.saveFilters}
      />
    ))
  ) : (
    <Grid item xs={12}>
      <div className="padding20">
        <Typography align="center" className="colorBlue">
          <WarningIcon id="warning" />{" "}
        </Typography>
        <Typography variant="h4" align="center" gutterBottom>
          No profiles found.
        </Typography>
        <Typography variant="subtitle1" align="center">
          Try adjusting your search.
        </Typography>
      </div>
    </Grid>
  );

    if (profiles === null || loading) {
      profileItems = <ProgressSpinner />;
    } else {
      //if text in search bar, display search results
      profileItems = (
        <Grid container spacing={10}>
          {profileContent}
        </Grid>
      );
    }

    return (
      <React.Fragment>
        <header>
          <Paper className="paddingMargin">
            <Grid container spacing={10}>
              <Grid item xs={12}>
              <Input
                id="search"
                placeholder="Name, Major, or Course"
                value={this.state.searchText}
                onChange={this.handleChange}
                startAdornment={
                  <InputAdornment position="start">
                    <SearchIcon />
                  </InputAdornment>
                }
                fullWidth
              />
              </Grid>
              <Grid item xs={3}>
                <Button
                  variant="outlined"
                  aria-haspopup="true"
                  onClick={this.handleMajorsMenu}
                  aria-owns={majorsDropDown ? "majorsMenu" : undefined}
                  fullWidth
                >
                  <div className="large">Subjects</div>
                  <div className="small">
                    <SubjectIcon />
                  </div>
                </Button>
                <Menu
                  id="majorsMenu"
                  anchorEl={majorsDropDown}
                  open={Boolean(majorsDropDown)}
                  onClose={this.closeMajorsMenu}
                  PaperProps={{ style: { maxHeight: 300 } }}
                >
                  {courseMenuItems}
                </Menu>
              </Grid>
              <Grid item xs={3}>
                <Button
                  variant="outlined"
                  aria-haspopup="true"
                  onClick={this.handleFilterBy}
                  aria-owns={filterDropDown ? "filterByMenu" : undefined}
                  fullWidth
                >
                  <div className="large">Paid or Volunteer</div>
                  <div className="small">
                    <PaidIcon />
                  </div>
                </Button>
                <Menu
                  id="filterByMenu"
                  anchorEl={filterDropDown}
                  open={Boolean(filterDropDown)}
                  onClose={this.closeFilterMenu}
                >
                  <MenuItem
                    value="Paid"
                    onClick={this.filterByPaid()}
                    variant="outlined"
                    name="Paid"
                  >
                    {" "}
                    Paid
                  </MenuItem>
                  <MenuItem
                    value="Volunteer"
                    onClick={this.filterByVolunteer()}
                    variant="outlined"
                    name="Volunteer"
                  >
                    {" "}
                    Volunteer
                  </MenuItem>
                </Menu>
              </Grid>
              <Grid item xs={3}>
                <Button
                  variant="outlined"
                  aria-haspopup="true"
                  onClick={this.handleOrderBy}
                  aria-owns={orderDropDown ? "orderByMenu" : undefined}
                  fullWidth
                >
                  <div className="large">Order by</div>
                  <div className="small">
                    <SortIcon />
                  </div>
                </Button>
                <Menu
                  id="orderByMenu"
                  anchorEl={orderDropDown}
                  open={Boolean(orderDropDown)}
                  onClose={this.closeOrderMenu}
                >
                  <MenuItem onClick={this.orderByName}> First Name</MenuItem>
                  <MenuItem onClick={this.orderByMajor}> Major</MenuItem>
                </Menu>
              </Grid>
              <Grid item xs={3}>
                <Button variant="outlined" onClick={this.shuffle} fullWidth>
                  <div className="large">Shuffle</div>
                  <div className="small">
                    <ShuffleIcon />
                  </div>
                </Button>
              </Grid>
            </Grid>
            <Grid item xs={12}>
              {this.state.filterByVolunteer && (
                <Chip
                  className="search-chip"
                  variant="outlined"
                  label="Volunteer"
                  onDelete={this.removeVolunteerFilter()}
                />
              )}
              {this.state.filterByPaid && (
                <Chip
                  className="search-chip"
                  variant="outlined"
                  label="Paid"
                  onDelete={this.removePaidFilter()}
                />
              )}
              {subjectChips}
            </Grid>
          </Paper>
        </header>
        <div>{profileItems}</div>
      </React.Fragment>
    );
  }
}

ProfilesShowcase.propTypes = {
  profile: PropTypes.object.isRequired,
  subjects: PropTypes.object.isRequired,
};

const mapStateToProps = (state) => ({
  profile: state.profile,
  subjects: state.subjects,
});

export default connect(
  mapStateToProps,
  { getProfiles, getSubjects }
)(withStyles(styles)(ProfilesShowcase));
