import React from "react";
import PropTypes from "prop-types";
import { Link } from "react-router-dom";

// redux import
import { connect } from "react-redux";

// MUI imports
import { withStyles } from "@material-ui/core/styles";
import Card from "@material-ui/core/Card";
import CardHeader from "@material-ui/core/CardHeader";
import CardActions from "@material-ui/core/CardActions";
import CardContent from "@material-ui/core/CardContent";
import Button from "@material-ui/core/Button";
import Typography from "@material-ui/core/Typography";
import Chip from "@material-ui/core/Chip";
import Avatar from "@material-ui/core/Avatar";
import Grid from "@material-ui/core/Grid";
import Tooltip from "@material-ui/core/Tooltip";
import MailIcon from "@material-ui/icons/Email";
import CalendarIcon from "@material-ui/icons/EventAvailable";
import InfoIcon from "@material-ui/icons/Info";

const styles = (theme) => ({
  card: {
    maxWidth: 300,
    maxHeight: 300,
  },
});

const truncateText = (text) => {
  if (text.length > 40) {
    const shortText =
      text
        .split(" ")
        .splice(0, 6)
        .join(" ") + "...";
    return shortText;
  } else if (text.length < 1) {
    return "N.A.";
  }

  return text;
};

const ProfileItem = (props) => {
  const { profile, auth, onClick } = props;

  const classesCard =
    profile.courses !== undefined ? (
      <React.Fragment>
        {profile.courses.slice(0, 5).map((myClass, index) => (
          <Tooltip key={index} title={myClass.courseName}>
            <Chip
              className="chip"
              label={`${myClass.courseId} ${myClass.courseNumber}`}
            />
          </Tooltip>
        ))}
      </React.Fragment>
    ) : (
      <p>No classes listed</p>
    );

  // Create initials and short version of name for use on card
  const firstname =
    profile.user.firstname === null ? "NA" : profile.user.firstname;
  const lastname =
    profile.user.lastname === null ? "NA" : profile.user.lastname;
  const initials =
    firstname && lastname ? firstname.charAt(0) + lastname.charAt(0) : "";
  const shortname =
    firstname && lastname ? firstname + " " + lastname.charAt(0) + "." : "";

  const profileEmail = profile.user.email === null ? "NA" : profile.user.email;

  const majors = profile.major.join(", ");
  const minors = profile.minor.length > 0 ? profile.minor.join(", ") : "";
  const headerText =
    profile.type === "Paid" ? (
      <span>
        {shortname}
        <span className="tag">$$$</span>
      </span>
    ) : (
      <span>{shortname}</span>
    );
  const subheaderText =
    minors.length > 0 ? majors + " (" + minors + ")" : majors;

  return (
    <React.Fragment>
      <Grid item xs={12} sm={6} md={4}>
        <Card>
          <CardHeader
            className="cardHeader"
            avatar={<Avatar className="purpleAvatar">{initials}</Avatar>}
            title={headerText}
            subheader={subheaderText}
          />
          <CardContent>
            <div className="spaceBelow">{classesCard}</div>
            <Grid container wrap="nowrap" spacing={8}>
              <Grid item>
                <MailIcon className="icon" />
              </Grid>
              <Grid item xs>
                <Typography>{profileEmail}</Typography>
              </Grid>
            </Grid>
            {profile.bio && (
              <Grid container wrap="nowrap" spacing={8}>
                <Grid item>
                  <InfoIcon className="icon" />
                </Grid>
                <Grid item xs>
                  <Typography>{truncateText(profile.bio)}</Typography>
                </Grid>
              </Grid>
            )}
            {profile.availability && (
              <Grid container wrap="nowrap" spacing={8}>
                <Grid item>
                  <CalendarIcon className="icon" />
                </Grid>
                <Grid item xs>
                  <Typography>{truncateText(profile.availability)}</Typography>
                </Grid>
              </Grid>
            )}
          </CardContent>
          <CardActions>
            <Button
              component={Link}
              onClick={onClick}
              size="small"
              color="inherit"
              to={`/profile/${profile.handle}`}
            >
              View Profile
            </Button>
            {profile.user._id !== auth.user.id && (
              <Button
                size="small"
                className="colorPurple"
                href={`mailto:${profile.user.email}`}
              >
                Email Tutor
              </Button>
            )}
            {auth.isAuthenticated && profile.user._id === auth.user.id && (
              <Button
                component={Link}
                size="small"
                to={`/edit-profile`}
                className="colorPurple"
              >
                Edit Profile
              </Button>
            )}
          </CardActions>
        </Card>
      </Grid>
    </React.Fragment>
  );
};

ProfileItem.propTypes = {
  profile: PropTypes.object.isRequired,
  auth: PropTypes.object.isRequired,
  onClick: PropTypes.func.isRequired,
};

const mapStateToProps = (state) => ({
  auth: state.auth,
});

export default connect(mapStateToProps)(withStyles(styles)(ProfileItem));
